condition.arg:
					    logger = transientVars.get("logger");

                        propertySet.setString("result", "denied");

                        logger.debug("================");
                        logger.debug("LM Step: " + jn.get(200));
                        logger.debug("HR Step: " + jn.get(300));

                        logger.debug("Step 200 LM status: " + jn.get(200).getStatus());
                        logger.debug("Step 300 HR status: " + jn.get(300).getStatus());

                        if(jn.get(200).getStatus().endsWith("approved") && 
                           jn.get(300).getStatus().endsWith("approved")) {
                            propertySet.setString("result", "approved");
                        }

                        // Final result
                        !("Underway".equals(jn.get(200).getStatus())) && 
                        !("Underway".equals(jn.get(300).getStatus()));

function.arg:
					    logger = transientVars.get("logger");
 	                    logger.debug("Join 10000 finished - setting result to '${result}'");
                 	    propertySet.setString("result", "${result}");
